import React from 'react'
import { NavLink, useHistory, useParams } from 'react-router-dom'
import cx from './scroll.module.css'
import * as queryString from 'querystring'
import { Link } from 'react-scroll'


export const HorizontalScroll = (props) => {

    const history = useHistory();
    // debugger

        const pushURL = (id) => {
            history.push({
                pathname: `/category/dishes`,
                search: `?dish=${id}`,

            })
            props.setPages(id);
            // document.getElementById(`scrollCont${id}`).classList.add(cx.ScrollItemActive)


        }
    return (

        <div  className={cx.ScrollContainer}>
            {props.categories.map((el) => {
                return <Link to = {el.name} smooth = {instant} duration = {2000}> <button id = {`scrollCont${el.id}`} onClick = {() => {pushURL(el.id)}} className={cx.ScrollItem}>
                    {el.name}
                </button></Link>


            })}


        </div>



    )
}
