import React, { useCallback, useEffect } from 'react'
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import { useParams } from 'react-router';
import { CircularProgress } from '@material-ui/core';
import { getDish } from './ducks/reducer';
import Header from '../main/header/Header';
import { HorizontalScroll } from './horizontalScroll/scroll';
import { getCategory } from '../../api';
import { getCat } from '../main/categories/ducks/reducer';
import DishItem from './dishItem/DishItem';
import { useState } from 'react';

const Dishes = () => {
    const dispatch = useDispatch();
    const param = useParams();
    const dishesOfcategory = useSelector(state => state.dish.dishes);
    const isFetching = useSelector(state => state.dish.isFetching);
    const ValueOfcategories = useSelector(state => state.category.categories);

    const [categories, setCatwgories ] = useState(ValueOfcategories)


    debugger
    console.log(dishesOfcategory)

    useEffect(() => {
        getCat();
        if(Number(param.id) - 1 != 0){
            dispatch(getDish(Number(param.id) - 1));
        }
        dispatch(getDish(param.id));
        dispatch(getDish(Number(param.id) + 1));

    }, []);

    return (
        <div >
            <Header />
            <HorizontalScroll categories={categories} />


            {isFetching === false ?
                dishesOfcategory.map(dish => {
                    return <div key = {dish.id}> {dish.name} {(dish.dishes.map((el) => {
                        return <DishItem dish={el} key={el.id} />
                    }))}</div>
                }) : <CircularProgress size={60} style={{ display: 'block', margin: '4rem auto' }} />}
        </div>
    )
}
export default Dishes;
