import { getDishes } from "../../../api";

const SET_DISH = "SET_DISH";
const SET_IS_FETCHING = "SET_IS_FETCHING";

const initialState = {
	isFetching: true,
	dishes: [],
};

export default function dishReducer(state = initialState, action) {
	debugger
	switch (action.type) {

		case SET_DISH:

			const dishIndex = state.dishes.findIndex((dish) => dish.id === action.payload.id)
			debugger
			if (dishIndex == -1) {
				debugger
				return {
					...state,
					dishes: [ ...state.dishes , action.payload],
					isFetching: false
				}
			}







		case SET_IS_FETCHING:
			return { ...state, isFetching: action.data };
		default:
			return state;
	}
}

export const setDish = (dish) => ({ type: SET_DISH, payload: dish });
export const setIsFetching = (data) => ({
	type: SET_IS_FETCHING,
	data

});

export const getDish = (id) => {
	return async (dispatch) => {
		dispatch(setIsFetching(true));
		const response = await getDishes(id);
		dispatch(setDish(response));
		// debugger
	};
};
